(()=>{"use strict";new class{constructor(){this.slides=document.querySelectorAll(".slide"),this.prevButton=document.querySelector(".prev"),this.nextButton=document.querySelector(".next"),this.currentSlide=0,this.maxSlide=this.slides.length-1,document.querySelector(".slider-wrapper")&&this.events()}events(){window.addEventListener("DOMContentLoaded",this.positionSlides.bind(this)),this.prevButton.addEventListener("click",(()=>{clearTimeout(this.timer),this.moveBack()})),this.nextButton.addEventListener("click",(()=>{clearTimeout(this.timer),this.moveForward()}))}positionSlides(){this.slides.forEach(((e,t)=>{e.style.transform=`translateX(${100*t}%)`,"translateX(0%)"==e.style.transform&&e.classList.add("active")}))}moveForward(){this.currentSlide===this.maxSlide?this.currentSlide=0:this.currentSlide++,this.slides.forEach(((e,t)=>{e.style.transform=`translateX(${100*(t-this.currentSlide)}%)`,"translateX(0%)"!==e.style.transform?e.classList.remove("active"):e.classList.add("active")}))}moveBack(){0===this.currentSlide?this.currentSlide=this.maxSlide:this.currentSlide--,this.slides.forEach(((e,t)=>{e.style.transform=`translateX(${100*(t-this.currentSlide)}%)`,"translateX(0%)"!==e.style.transform?e.classList.remove("active"):e.classList.add("active")}))}},new class{constructor(){this.slides=document.querySelectorAll(".video-slide"),this.prevBtn=document.querySelector(".prev-video"),this.nextBtn=document.querySelector(".next-video"),this.currentVideoSlide=0,document.querySelector(".video-slider-wrapper")&&this.events()}events(){window.addEventListener("DOMContentLoaded",(()=>{console.log("test")}))}}})();